#using tonelli algorithm 
def legendre(a, p):
    return pow(a, (p - 1) // 2, p)

def tonelli(n, p):
    r = []
    assert legendre(n, p) == 1, "not a square (mod p)"
    q = p - 1
    s = 0
    while q % 2 == 0:
        q //= 2
        s += 1
    if s == 1:
        return pow(n, (p + 1) // 4, p)
    for z in range(2, p):
        if p - 1 == legendre(z, p):
            break
    c = pow(z, q, p)
    r = pow(n, (q + 1) // 2, p)
    t = pow(n, q, p)
    m = s
    t2 = 0
    while (t - 1) % p != 0:
        t2 = (t * t) % p
        for i in range(1, m):
            if (t2 - 1) % p == 0:
                break
            t2 = (t2 * t2) % p
        b = pow(c, 1 << (m - i - 1), p)
        r = (r * b) % p
        c = (b * b) % p
        t = (t * c) % p
        m = i
    return r

def decrypt(m, expo):
  if expo == 1:
    flag = long_to_bytes(m)
    if b"crypto" in flag:
      print(flag)
      exit(0)
  else:
    r1 = tonelli(m, n)
    decrypt(r1, expo // 2)
    r2 = n - r1
    decrypt(r2, expo // 2)


decrypt(pow(ct,d,n),8)

